package leetcode

import "testing"
import "github.com/stretchr/testify/assert"

func Test1(t *testing.T) {
	type item struct {
		input    string
		expected string
	}

	cases := []item{
		item{"babad", "bab"},
		item{"cbbd", "bb"},
		item{"a", "a"},
		item{"bb", "bb"},
		item{"", ""},
	}

	for _, c := range cases {
		actual := longestPalindrome(c.input)
		assert.Equal(t, c.expected, actual, "%+v", c)
	}
}
